css:
a {
	color:#5f0099;
color:#F9F6EE;
}
a:hover {
	color:#5f0099;

}
h1, h2, h3 {
	font-weight: 700;
	color:#F9F6EE;
}
h4, h5 {
	font-weight:600;
color:#F9F6EE;
}
h6 {
	font-weight:500;
color:#F9F6EE;
}
.banner {
    background: #36B1C7;
  background: linear-gradient(
    to right,
    #36B1C7,#960B33
  );
	/*background: #7a60ff;*/
	/*background: linear-gradient(to left, #7a60ff, #cd9ffa);*/
	padding:100px 0px;
	position:relative;
}
.banner::before {
	content: "";
	display: block;
	position: absolute;
	left: 0;
	top: 0;
	width: 100%;
	height: 100%;
	background: url(images/pattern.png);
}
.banner a.weblink {
	color:#ffffff;
	border-bottom:1px dotted #ffffff;
}
.banner a:hover {
	text-decoration:none;
}
.svg-wave {
	position: absolute;

}
.banner-text {
	padding-right:80px;
	margin:50px 0px 0px;

}
.banner-text p {
    color:#F9F6EE;
	margin:40px 0px;
}
.banner-text ul {
	list-style: none;
}
.banner-text ul li {
	display: inline-block;
	margin: 0px 10px 10px 0px;
}
.banner-text ul li a {
	display: block;
}
.prelative {
	position:relative
}
.section-padding {
	padding:80px 0px;
}
.sectioner-header {
	width:69%;
	margin:0 auto;
}
.sectioner-header p {
	color:#818992;
	font-size: 17px;
}
.section-content {
	margin-top: 80px;
}
.icon-box {
	margin-bottom:50px;
}
.icon-box i {
	display: block;
	position: relative;
	width: 135px;
	height: 135px;
	border-radius: 100px;
	background: #36B1C7;
  background: linear-gradient(
    to right,
    #36B1C7,#960B33
  );
	/*background: #7a60ff;*/
	/*background:  linear-gradient(to left, #7a60ff, #cd9ffa);*/
	color: #ffffff;
	font-size: 50px;
	line-height: 135px;
	margin: 0 auto;
}
.icon-box h5 {
	margin-top:30px;
}
.icon-box p {
	color: #818992;
	font-size: 14px;
	width: 80%;
	margin: 0 auto;
}
.about-btn {
	color: #8e1efc;
	padding: 10px 40px;
	border: 2px solid #8e1efc;
	border-radius: 7px;
	margin-top: 30px;
	display: inline-block;
	font-size: 18px;
}
.about-btn:hover {
	background:#8e1efc;
	color:#ffffff;
	text-decoration:none;
}


.contact-info {
    text-align:left;
    font-weight:500;
	padding: 2rem 2rem 1rem;
	border-radius: 8px;
	background: #36B1C7;
  background: linear-gradient(
    to right,
    #36B1C7,#960B33
  );
	/*background: #7a60ff;*/
	/*background: linear-gradient(to left, #7a60ff, #cd9ffa);*/
}
.contact-item {
	margin:23px 0px;
}
.contact-item i {
	font-size: 20px;

}
.contact-item p {
	line-height: 20px;
	margin: 0;
}
